import React from "react";
import "../App.css";
import Modal from "./Modal";
import ProfileModal from "./ProfileModal.js";
import "../css/followers-followinglist.css"

export default class FollowersList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal3: false,
      connections: [],
      error: null,
      userid: sessionStorage.getItem("user"),
      followername: "",
      showProfile: false,
    };
  }

  showFollowingModal = (e) => {
    this.setState({
      showModal3: !this.state.showModal3,
    });
  };

  onClose = (e) => {
    this.props.onClose && this.props.onClose(e);
  };

  loadConnections = () => {
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php", {
      method: "post",
      body: JSON.stringify({
        action: "getConnections",
        connectuserid: sessionStorage.getItem("user")
      })
    })
      .then(res => res.json())
      .then(
        result => {
          if (result.connections !== undefined) {
            this.setState({
              connections: result.connections
            });
          }
          console.log(result)
        },
        error => {
          this.setState({
            error
          });
        }
      );
  }

  showProfileModal = (e) => {
      this.setState({
        showProfile: !this.state.showProfile,
      });
  };

  getFollowerName(id){
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/usercontroller.php", {
        method: "post",
        body: JSON.stringify({
          action: "getConnections",
          userid: id,
        })
      })
      .then(res => res.json())
      .then(
        result => {
            if (result.users[0].name !== null) {
                this.setState({
                  followername: result.users[0].name,
                });
                // sessionStorage.setItem("dname", result.users[0].name);
              }
            },
            error => {
                this.setState({
                  error
                });
              }
            );
    }


  componentDidMount(){
    this.loadConnections()
  }

  render() {
    //this.loadPosts();
    const {error, connections} = this.state;
    let outerModal = React.createRef();

    if (error) {
      return <div> Error: {error.message} </div>;
    } else {
      return (
        <div>
          <ul>
            {connections.map(connection => (
              <div key={connection.connection_id} className="userlist" onClick={(e) => this.showProfileModal(e)}>
                {connection.user_name}
                <ProfileModal
                  show={this.state.showProfile}
                  onClose={(e) => this.showProfileModal(e)}
                  userid={connection.user_id}
                />
              </div>
            ))}
          </ul>
        </div>
      );
    }
  }
}
