import React from "react";
import "../App.css";
import Modal from "./Modal";
import ProfileModal from "./ProfileModal.js";
import "../css/followers-followinglist.css"

export default class FriendList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal2: false,
      connections: [],
      error: null,
      showProfile: false,
      currentProfile:"",
    };
  }

  showFollowingModal = (e) => {
    this.setState({
      showModal2: !this.state.showModal2,
    });
  };

  onClose = (e) => {
    this.props.onClose && this.props.onClose(e);
  };

  loadConnections = () => {
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php", {
      method: "post",
      body: JSON.stringify({
        action: "getConnections",
        userid: sessionStorage.getItem("user")
      })
    })
      .then(res => res.json())
      .then(
        result => {
          if (result.connections !== undefined) {
            this.setState({
              connections: result.connections
            });
          }
          console.log(result)
        },
        error => {
          this.setState({
            error
          });
        }
      );
  }

  showProfileModal = (e) => {
    this.setState({
      showProfile: !this.state.showProfile,
    });
  };


  setCurrentProfile(profile_id){
    this.setState({
      currentProfile: profile_id
    })
  }

  componentDidMount(){
    this.loadConnections()
  }

  render() {
    //this.loadPosts();
    const {error, connections} = this.state;
    let outerModal = React.createRef();
    if (error) {
      return <div> Error: {error.message} </div>;
    } else {
      return (
        <div>
          <ul>
          {connections.map(connection => (
              <div key={connection.connection_id} className="userlist" onClick={(e) => {this.showProfileModal(e);this.setCurrentProfile(connection.connect_user_id)}}>
                {connection.user_name}
              </div>
            ))}
            {console.log("testing this: " + this.state.currentProfile)}
            <ProfileModal
            show={this.state.showProfile}
            onClose={(e) => this.showProfileModal(e)}
            userid={this.state.currentProfile}
            />
          </ul>
        </div>
      );
    }
  }
}
