import React from "react";
import "../App.css";
import "../css/post.css";
import CommentForm from "./CommentForm.js";
import Modal from "./Modal";
import EditPost from "./EditPost";
import CommentList from "./CommentList.js";
import ProfileModal from "./ProfileModal.js";

class MainContent extends React.Component {
  _isMounted = false;
  constructor(props) {
    super(props);
    this.handler = this.handler.bind(this);
    this.state = {
      section: this.props.modalPage,
      comments: "",
    };
  }
  handler() {
    this.setState({
      section: "comment",
    });
  }
  onClose = (e) => {
    this.props.onClose && this.props.onClose(e);
  };

  deletePost = (event) => {
    //make the api call to the authentication page
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/postcontroller.php", {
      method: "post",
      body: JSON.stringify({
        action: "deletePosts",
        user_id: sessionStorage.getItem("user"),
        session_token: sessionStorage.getItem("token"),
        postid: this.props.postId,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          if (result.Status === "SUCCESS - Deleted 1 Rows") {
            window.location.reload(false);
          } else {
            console.log(result.Status);
          }
        },
        (error) => {
          alert("error!");
        }
      )
      .catch((err) => {
        console.error("Request failed");
      });
  };

  // handleChange(e) {
  //   this.setState({
  //     ...this.state,
  //     [e.target.name]: e.target.value
  //   });
  // }

  handleDelete(e) {
    this.setState({
      section: "delete",
    });
  }

  handleEdit(e) {
    this.setState({
      section: "edit",
    });
  }
  getCommentCount() {
    if (!this.state.comments || this.state.comments === "0") {
      return 0;
    }
    return parseInt(this.state.comments);
  }
  render() {
    if (this.state.section === "options") {
      return (
        <div className="modal-buttons">
          <div className="button1-container">
            <button className="button1" onClick={(e) => this.handleEdit(e)}>
              <span>Edit</span>
            </button>
          </div>
          <div className="button2-container">
            <button className="button2" onClick={(e) => this.handleDelete(e)}>
              <span>Delete</span>
            </button>
          </div>
        </div>
      );
    } else if (this.state.section === "delete") {
      return (
        <div>
          <div className="mod-title">Are You Sure?</div>
          <div className="modal-buttons">
            <div className="button1-container">
              <button className="button1" onClick={this.onClose}>
                <span>No</span>
              </button>
            </div>
            <div className="button2-container">
              <button className="button2" onClick={(e) => this.deletePost(e)}>
                <span>Yes</span>
              </button>
            </div>
          </div>
        </div>
      );
    } else if (this.state.section === "edit") {
      return (
        <div>
          <EditPost
            postId={this.props.postId}
            time={this.props.time}
            text={this.props.text}
            title={this.props.title}
            collab={this.props.collab}
            pic={this.props.pic}
            tag={this.props.tag}
            onClose={this.props.onClose}
          />
        </div>
      );
    } else if (this.state.section === "comment") {
      return (
        <div className="comment-modal">
          <CommentList
            posts={this.props.postId}
            error={this.props.error}
            isLoaded={this.props.isLoaded}
          />
          <div className="text-above-commentForm">
            <h1>Add your comment!</h1>
          </div>
          <CommentForm post={this.props.postId} handler={this.handler} />
        </div>
      );
    }
  }
}
