import React from "react";
import "../css/profilemodal.css";
import ProfilePostList from "./ProfilePostList";
export default class ProfileModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      followStatus: "Follow",
      dname: "",
      bio: "",
      crowdfunding: "",
      profilePic: "",
      posts: [],
      isNotBlocked: true,
      blockID: "",
      blockStatus: "",
    };
  }

  onClose = (e) => {
    this.props.onClose && this.props.onClose(e);
  };

  checkConnections = (userid) => {
    fetch(
      "http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php",
      {
        method: "post",
        body: JSON.stringify({
          action: "getConnections",
          userid: sessionStorage.getItem("user"),
          connectuserid: userid,
        }),
      }
    )
      .then((res) => res.json())
      .then((result) => {
        if (result.connections !== undefined) {
          this.setState({
            followStatus: "Unfollow",
          });
        }
      });
  };

  changeConnection = (e) => {
    if (this.state.followStatus === "Unfollow") {
      fetch(
        "http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php",
        {
          method: "post",
          body: JSON.stringify({
            action: "getConnections",
            userid: sessionStorage.getItem("user"),
            connectuserid: this.props.userid,
          }),
        }
      )
        .then((res) => res.json())
        .then((result) => {
          let connectionId = result.connections[0].connection_id;

          return fetch(
            "http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php",
            {
              method: "post",
              body: JSON.stringify({
                action: "deleteConnections",
                user_id: sessionStorage.getItem("user"),
                session_token: sessionStorage.getItem("token"),
                connectionid: connectionId,
              }),
            }
          )
            .then((res) => res.json())
            .then((result) => {
              if (result.Status.includes("SUCCESS")) {
                this.setState({
                  followStatus: "Follow",
                });
                window.location.reload(false);
              }
            });
        });
    } else if (this.state.isNotBlocked === false && this.state.followStatus === "Follow") {
      fetch(
        "http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php",
        {
          method: "post",
          body: JSON.stringify({
            action: "getConnections",
            userid: sessionStorage.getItem("user"),
            connectuserid: this.props.userid,
          }),
        }
      )
        .then((res) => res.json())
        .then((result) => {
          console.log(result);
          if (result.connections === undefined) {
            return fetch(
              "http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php",
              {
                method: "post",
                body: JSON.stringify({
                  action: "addOrEditConnections",
                  user_id: sessionStorage.getItem("user"),
                  session_token: sessionStorage.getItem("token"),
                  userid: sessionStorage.getItem("user"),
                  connectuserid: this.props.userid,
                }),
              }
            )
              .then((res) => res.json())
              .then((result) => {
                console.log(result);
                if (result.Status.includes("SUCCESS")) {
                  this.setState({
                    followStatus: "Unfollow",
                  });
                  window.location.reload(false);
                }
                this.deleteBlock()
              });
          }
        });
    } else {
      fetch(
        "http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php",
        {
          method: "post",
          body: JSON.stringify({
            action: "getConnections",
            userid: sessionStorage.getItem("user"),
            connectuserid: this.props.userid,
          }),
        }
      )
        .then((res) => res.json())
        .then((result) => {
          console.log(result);
          if (result.connections === undefined) {
            return fetch(
              "http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php",
              {
                method: "post",
                body: JSON.stringify({
                  action: "addOrEditConnections",
                  user_id: sessionStorage.getItem("user"),
                  session_token: sessionStorage.getItem("token"),
                  userid: sessionStorage.getItem("user"),
                  connectuserid: this.props.userid,
                }),
              }
            )
              .then((res) => res.json())
              .then((result) => {
                console.log(result);
                if (result.Status.includes("SUCCESS")) {
                  this.setState({
                    followStatus: "Unfollow",
                  });
                  window.location.reload(false);
                }
              });
          }
        });
    }
  };

  getUserArtifacts = (artifact_cat) => {
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/uacontroller.php", {
      method: "post",
      body: JSON.stringify({
        action: "getUserArtifacts",
        userid: this.props.userid,
        artifactcategory: artifact_cat,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          if (artifact_cat === "profilePic") {
            this.setState({
              profilePic: result.user_artifacts[0].artifact_url,
            });
          } else if (artifact_cat === "bio") {
            this.setState({
              bio: result.user_artifacts[0].artifact_url,
            });
          } else if (artifact_cat === "crowdfunding") {
            this.setState({
              crowdfunding: result.user_artifacts[0].artifact_url,
            });
          }
        },
        (error) => {
          alert("error!");
        }
      );
  };

  checkIfBlocked(userid) {
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/uacontroller.php", {
      method: "post",
      body: JSON.stringify({
        action: "getUserArtifacts",
        userid: sessionStorage.getItem("user"),
        artifacturl: userid,
        artifacttype: "block-list2",
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          if (result.user_artifacts != null) {
            this.setState({
              isNotBlocked: false,
              blockStatus: "Blocked",
              blockID: result.user_artifacts[0].artifact_id,
            });
          } else {
            this.setState({
              isNotBlocked: true,
              blockStatus: "Block",
            });
          }
        }
        // (error) => {
        //   alert("error!");
        // }
      );
  }

  getUsername = (userid) => {
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/usercontroller.php", {
      method: "post",
      body: JSON.stringify({
        action: "getUsers",
        userid: userid,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            dname: result.users[0].name,
          });
        },
        (error) => {
          alert("error!");
        }
      );
  };

  getUserPosts = (userid) => {
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/postcontroller.php", {
      method: "post",
      body: JSON.stringify({
        action: "getPosts",
        userid: userid,
        max_posts: "5",
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          if (result.posts) {
            this.setState({
              posts: result.posts,
            });
          }
        },
        (error) => {}
      );
  };

  addBlock() {
    if (this.state.followStatus === "Unfollow") {
    fetch(
      "http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php",
      {
        method: "post",
        body: JSON.stringify({
          action: "getConnections",
          userid: sessionStorage.getItem("user"),
          connectuserid: this.props.userid,
        }),
      }
    )
      .then((res) => res.json())
      .then((result) => {
        let connectionId = result.connections[0].connection_id;

        return fetch(
          "http://stark.cse.buffalo.edu/cse410/atam/api/connectioncontroller.php",
          {
            method: "post",
            body: JSON.stringify({
              action: "deleteConnections",
              user_id: sessionStorage.getItem("user"),
              session_token: sessionStorage.getItem("token"),
              connectionid: connectionId,
            }),
          }
        )
          .then((res) => res.json())
          .then((result) => {
            if (result.Status.includes("SUCCESS")) {
              this.setState({
                followStatus: "Follow",
              });
              window.location.reload(false);
            }
            return fetch("http://stark.cse.buffalo.edu/cse410/atam/api/uacontroller.php", {
              method: "post",
              body: JSON.stringify({
                action: "addOrEditUserArtifacts",
                user_id: sessionStorage.getItem("user"),
                session_token: sessionStorage.getItem("token"),
                userid: sessionStorage.getItem("user"),
                artifacturl: this.props.userid,
                artifacttype: "block-list2",
              }),
            })
            .then((res) => res.json())
            .then((result) => {
              this.setState({
                blockID: result["Record Id"],
                blockStatus: "Blocked",
                isNotBlocked: false,
              });
              console.log(result);
              // console.log(this.state.artifactID);
            });
          });
