import React from "react";
import "../css/Notification.css";

export default class Notification extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      title: "",
      caption: "",
      collab: "",
      modalPage: "",
      user: "",
    };
    // this.post = React.createRef();
  }

  getUser(userid) {
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/usercontroller.php", {
      method: "post",
      body: JSON.stringify({
        action: "getUsers",
        userid: userid,
      }),
    })
      .then((res) => res.json())
      .then((result) => {
        if (result.users) {
          this.setState({
            user: result.users[0],
          });
        }
      });
  }
  componentDidMount() {
    this.getUser(this.props.notification.user_id);
  }
  render() {
    console.log(this.props.notification);
    return (
      // <div key={this.props.post.post_id}>
      //   {this.props.post.user_id} {this.props.post.timestamp} <br />{" "}
      //   {this.props.post.post_text}

      <div>
        <p className="notifblock">{this.props.notification.artifact_url}</p>
      </div>
    );
  }
}
